#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\rscok\OneDrive - Haute Ecole Louvain en Hainaut\Bureau\patro-app\node_modules\json5\lib\node_modules;C:\Users\rscok\OneDrive - Haute Ecole Louvain en Hainaut\Bureau\patro-app\node_modules\json5\node_modules;C:\Users\rscok\OneDrive - Haute Ecole Louvain en Hainaut\Bureau\patro-app\node_modules;C:\Users\rscok\OneDrive - Haute Ecole Louvain en Hainaut\Bureau\node_modules;C:\Users\rscok\OneDrive - Haute Ecole Louvain en Hainaut\node_modules;C:\Users\rscok\node_modules;C:\Users\node_modules;C:\node_modules;C:\Users\rscok\OneDrive - Haute Ecole Louvain en Hainaut\Bureau\patro-app\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/proc/cygdrive/c/Users/rscok/OneDrive - Haute Ecole Louvain en Hainaut/Bureau/patro-app/node_modules/json5/lib/node_modules:/proc/cygdrive/c/Users/rscok/OneDrive - Haute Ecole Louvain en Hainaut/Bureau/patro-app/node_modules/json5/node_modules:/proc/cygdrive/c/Users/rscok/OneDrive - Haute Ecole Louvain en Hainaut/Bureau/patro-app/node_modules:/proc/cygdrive/c/Users/rscok/OneDrive - Haute Ecole Louvain en Hainaut/Bureau/node_modules:/proc/cygdrive/c/Users/rscok/OneDrive - Haute Ecole Louvain en Hainaut/node_modules:/proc/cygdrive/c/Users/rscok/node_modules:/proc/cygdrive/c/Users/node_modules:/proc/cygdrive/c/node_modules:/proc/cygdrive/c/Users/rscok/OneDrive - Haute Ecole Louvain en Hainaut/Bureau/patro-app/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../json5/lib/cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../json5/lib/cli.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../json5/lib/cli.js" $args
  } else {
    & "node$exe"  "$basedir/../json5/lib/cli.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
